import React, {useState} from 'react';
import './App.css';
import './assets/andrei.jpg';
import java from './assets/java.png';
import C from './assets/C.png';
import Cplus from './assets/C++.png';
import JS from './assets/JS.png';
import Matlab from './assets/matlab.png';
import etap from './assets/etap.svg';
import { CSSTransition} from 'react-transition-group';



function Home() {
  const [javaLang, setJava] = useState(false);
  const [cLang, setC] = useState(false);
  const [cplus, setCplus] = useState(false);
  const [js, setJs] = useState(false);

  const handleToggle = () =>{
    setToggle(!toggled)
  }
  const handleJava = () =>{
    setC(false);
    setCplus(false);
    setJs(false);
    setJava(!javaLang);
  }
  const handleC = () =>{
    setC(!cLang);
    setCplus(false);
    setJs(false);
    setJava(false);
  }
  const handleCplus = () =>{
    setC(false);
    setCplus(!cplus);
    setJs(false);
    setJava(false);
  }
  const handleJs = () =>{
    setC(false);
    setCplus(false);
    setJava(false);
    setJs(!js);
  }


  const Panel = (props) =>{
    return(
          <div className="App-accordian">
            <p className="accordName">{props.name}</p>
            <p>{props.children}</p>
          </div>
      
    );
  }


  return (
    <div id="home">
      <div className="App-aboutBG">
        <p className="App-aboutName">About Me</p>

        <content className="App-body">
          <p>My Name is Andrei Frunza.</p>
          
          <p>I am a graduate from Ryerson University, having recently completed my bachelors in Electrical Engineering.</p>
            
          
          <p>Throughout my time at Ryerson, I worked in the following languages,</p>
          
          <div className="App-links">
            <img onClick={handleToggle} src={java} className="App-ImageR"/>
            
            <img src={C} className="App-ImageR"/>
            
            <img src={Cplus} className="App-ImageR"/>
            <img src={JS} className="App-ImageR"/>
            {toggled &&<Panel name="Java">This is what we're expanding</Panel>}
            {toggled &&<Panel name="C">This is what we're expanding</Panel>}
            {toggled &&<Panel name="C++">This is what we're expanding</Panel>}
            {toggled &&<Panel name="JavaScript">This is what we're expanding</Panel>}
          </div>
        
          <p>and with the following programs</p>

          <div className="App-links">
            <img src={Matlab} className="App-ImageR"/>
            <img src={etap} className="App-ImageR"/>
      
          </div>

        </content>
 
      </div>
      
      
    </div>
  );
}

export default Home;
